INSERT INTO public.tag (id, created_at, updated_at, title, tag_category_id, description, acronym) VALUES (39, '2021-10-03 05:02:16.499319+00', '2021-10-03 05:02:16.499319+00', 'Container, Apollo Lunar Sample Return (ALSRC), accessories', 2, NULL, NULL);
INSERT INTO public.tag (id, created_at, updated_at, title, tag_category_id, description, acronym) VALUES (40, '2021-10-03 05:02:16.499319+00', '2021-10-03 05:02:16.499319+00', 'Container, Apollo Lunar Sample Return (ALSRC)', 2, 'The Apollo Lunar Sample Return Container preserves a lunar-like vacuum around the samples and protects them from shock during the return flight and until they are opened in the Lunar Receiving Laboratory.  On the moon, while samples are being loaded, the seals are removed just prior to closing the box.  The ALSRC is held in a fixture at waist level to aid the astronauts in closing the cam latches. ', 'ALSRC, rock box, SRC');
INSERT INTO public.tag (id, created_at, updated_at, title, tag_category_id, description, acronym) VALUES (41, '2021-10-03 05:02:16.499319+00', '2021-10-03 05:02:16.499319+00', 'Container, Apollo Lunar Sample Return (ALSRC)', 2, 'The Apollo Lunar Sample Return Container preserves a lunar-like vacuum around the samples and protects them from shock during the return flight and until they are opened in the Lunar Receiving Laboratory.  On the moon, while samples are being loaded, the seals are removed just prior to closing the box.  The ALSRC is held in a fixture at waist level to aid the astronauts in closing the cam latches. ', 'ALSRC, rock box, SRC');
INSERT INTO public.tag (id, created_at, updated_at, title, tag_category_id, description, acronym) VALUES (42, '2021-10-03 05:02:16.499319+00', '2021-10-03 05:02:16.499319+00', 'Container, Core Sample Vacuum (CSVC)', 2, 'The CSVC is a derivative of te Special Environmental Sample Container, elongated to accomodate a 4-cm diameter drive tube. Same materials as SESC.', 'CSVC');
INSERT INTO public.tag (id, created_at, updated_at, title, tag_category_id, description, acronym) VALUES (43, '2021-10-03 05:02:16.499319+00', '2021-10-03 05:02:16.499319+00', 'Container, Special Environment Sample (SESC)', 2, 'The SESC can and lid were made from 304L stainless steel.  The indium alloy seal in the lid was indium with 10% silver, and the seal protectors were sheet teflon.
INSERT INTO public.tag (id, created_at, updated_at, title, tag_category_id, description, acronym) VALUES (44, '2021-10-03 05:02:16.499319+00', '2021-10-03 05:02:16.499319+00', 'Drill System (ALSD)', 2, 'The astronaut first attaches the handle (which also serves as an "on/off" switch) to the power head with battery.  Then he sets this aside while he assembles the bit, lower core stem and one or two upper core stems.  These are attached to the power head and drilled ino the regolith.  The power head is detahed and one or two more upper core stems are added.   The power head is re-attached ad drilling continued.  When the desired depth is achieved, the drill is briefly powered at that depth to clear the flutes of "cuttings".  The power head is removed, the treadle is installed over the protruding stems, and the drill sting is jacked out of the soil.  The string is placed horizontally in a fixture on the rear of the rover.  Exposed ends are capped as the string is broken into 2 or 3 sections for packing.', 'Apollo Lunar Surface Drill');
INSERT INTO public.tag (id, created_at, updated_at, title, tag_category_id, description, acronym) VALUES (45, '2021-10-03 05:02:16.499319+00', '2021-10-03 05:02:16.499319+00', 'Drill, Stem, upper', 2, 'The exterior diameter of the drill stems is 2.5 cm, while the interior diameter is 2.0 cm.  The length of an upper stem tube is 42.5 cm, which includes 2.5 cm overlap where the tubes screw together.  Thus each tube is capable of holding a column of soil 40 cm long.  The lower stem tube is shorter because the bit is attached to it.  The lower stem tube is 39.0 cm long, and the bit is 6.0 cm long.  When the bit is attached to the lower stem tube the length is 42.5 cm., lke an upper stem tube.', NULL);
